package song.api.common.base;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.CorsRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;/** * description: * author:          song * createDate:      2018/4/13 */@Configurationpublic class BaseController extends WebMvcConfigurerAdapter  {    @Override    public void addCorsMappings(CorsRegistry registry) {        registry.addMapping("/**")                .allowedOrigins("*")                .allowedMethods("GET", "POST", "PUT", "OPTIONS", "DELETE", "PATCH")                .allowCredentials(true).maxAge(3600);    }}