package song.api.user.controller;import io.swagger.annotations.Api;import io.swagger.annotations.ApiImplicitParam;import io.swagger.annotations.ApiImplicitParams;import io.swagger.annotations.ApiOperation;import lombok.extern.slf4j.Slf4j;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import song.api.common.base.BaseController;import song.api.common.result.ActionResult;import song.api.user.service.IDemoService;import song.api.user.model.Demo;import java.util.List;/** * description: * author:          song * createDate:      2017/12/28 */@RequestMapping("/demo")@RestController@Slf4jpublic class DemoController extends BaseController {    @Autowired    private IDemoService demoService;    @ApiOperation(value = "获取Demo",notes = "根据name获取用户信息列表")    @ApiImplicitParams(            @ApiImplicitParam(name = "name",value = "用户名",required = true,dataType = "String")    )    @GetMapping(value = "/list")    public List<Demo> getList(String name,                              @RequestParam(defaultValue = "1") int pageIndex,                              @RequestParam(defaultValue = "10") int pageSize) {        return demoService.getByName(name).getData();    }    @GetMapping(value = "/save")    public ActionResult save() {        demoService.save();        return new ActionResult(true);    }}