package song.api.user.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import song.api.user.base.BaseController;import song.api.user.model.Demo;import song.api.user.service.IDemoService;import java.util.List;/** * description: * author:          song * createDate:      2017/12/28 */@RequestMapping("/demo")@RestControllerpublic class DemoController extends BaseController {    @Autowired    private IDemoService demoService;    @GetMapping(value = "/list")    public List<Demo> getList(String name,                              @RequestParam(defaultValue = "1") int pageIndex,                              @RequestParam(defaultValue = "10") int pageSize) {        return demoService.getByName(name);    }}