package com.song.common;/** * description:     处理结果基类 * author:          song * createDate:      2017/9/25 */public class Result {    private boolean isSuccess=true;    private String msg;    public Result(boolean isSuccess, String msg) {        this.isSuccess = isSuccess;        this.msg = msg;    }    public Result(boolean isSuccess) {        this.isSuccess = isSuccess;    }    public Result() {    }    public boolean isSuccess() {        return isSuccess;    }    public void setSuccess(boolean success) {        isSuccess = success;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }}