package com.song.common;import java.util.HashMap;/** * description:     处理结果基类 * author:          song * createDate:      2017/9/25 */public class Result {    private boolean isSuccess;    private String msg;    private HashMap<String, String> attrs = new HashMap<String, String>();    public Result(boolean isSuccess, String msg) {        setSuccess(isSuccess);        setMsg(msg);    }    public Result(boolean isSuccess) {        setSuccess(isSuccess);    }    public boolean getIsSuccess() {        return isSuccess;    }    public void setSuccess(boolean success) {        isSuccess = success;        this.add("isSuccess", success);    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;        this.add("msg", msg);    }    public Result add(String key, Number value) {        attrs.put(key, value.toString());        return this;    }    public Result add(String key, Boolean value) {        attrs.put(key, value.toString());        return this;    }    public Result add(String key, String value) {        attrs.put(key, "\"" + value + "\"");        return this;    }    public void remove(String key) {        if (attrs.containsKey(key)) {            attrs.remove(key);        }    }    protected HashMap<String, String> getAttrs() {        return this.attrs;    }}