package com.song.io;import com.song.lang.ArrayHelper;/** * description: * author:          song * createDate:      2017/10/31 */public enum FileType {    Excel("xls", "xlsx"),    Word("doc", "docx"),    PPT("ppt", "pptx"),    Image("jpg", "jpeg", "png", "gif", "bmp", "tiff", "ico"),    Txt("txt"),    PDF("pdf"),    Html("htm", "html", "css", "js"),    Flash("swf", "fla", "as"),    Design("rp", "dwg", "psd", "cdr", "dxf", "dxb", "col", "ai", "eps"),    Config("xml", "config", "sitemap", "cfg", "ini", "properties","iml","mf"),    DotNet("aspx", "cshtml", "cs", "ashx", "asp", "xaml", "ascx", "master", "asmx", "asax", "rpt", "xsd", "resx", "dll"),    Java("java", "jsp", "jar", "class", "war"),    Package("zip", "rar", "7z", "gzip","tar","gz"),    DB("db", "sql", "mdf", "accdb", "mdb", "ldf", "sdf"),    Binary("bin", "obj"),    Execute("exe", "bat", "msi", "iso", "apk", "com","sh"),    BT("bt", "torrent"),    Video("mp4", "rm", "rmvb", "avi", "flv", "mkv", "mov", "mpeg", "mpg", "qt", "ram", "viv", "wmv", "asf", "wm", "wmp", "rpm", "dat", "amv", "m3u8"),    Voice("mp3", "wma", "aif", "aiff", "amr", "mp5", "mpa", "ogg", "wav", "mid", "mpc"),    UML("vsd", "vsdx", "pdm", "oom", "pdb", "cdm", "bpm", "pam", "mmap", "xmind"),    Other("", "*");    private String[] extensions;    public String[] getExtensions() {        return extensions;    }    FileType(String... extensionNames) {        this.extensions = new String[extensionNames.length];        for (int i = 0; i < extensionNames.length; i++) {            this.extensions[i] = extensionNames[i];        }    }    /**     * 将扩展名解析为文件类型     *     * @param extensionName     * @return     */    public static FileType parse(String extensionName) {        FileType[] fileTypes = values();        for (FileType fileType : fileTypes) {            if (ArrayHelper.contains(extensionName, fileType.getExtensions())) {                return fileType;            }        }        return FileType.Other;    }    /**     * 判断扩展名是否存在于文件类型     *     * @param extensionName     * @return     */    public boolean contains(String extensionName) {        return ArrayHelper.contains(extensionName, extensions);    }}