package com.song.net.http;import com.song.util.RandomText;/** * description:     HttpMultipart * author:          song * createDate:      2017/9/28 */public class HttpMultipart {    private static final String PARTINGLINE="---------------------------";    public HttpMultipart() {        RandomText randomText = new RandomText();        randomText.setAllowLowerCase(true);        String text= randomText.next(10);        String b=NEWLINE+WRAP+PARTINGLINE+text;        this.boundary=b+NEWLINE;        this.boundaryEnd=b+WRAP+NEWLINE;        this.contentType = "multipart/form-data; boundary=" + PARTINGLINE+text;    }    public static final String WRAP="--";    public static final String NEWLINE="\\r\\n";    private String boundary;    private String boundaryEnd;    private String contentType;    public String getContentType() {        return contentType;    }    public String getBoundaryEnd() {        return boundaryEnd;    }    public String getBoundary() {        return boundary;    }    public String getDisposition(String name) {        return getBoundary()+"Content-Disposition: form-data; name=\"" + name + "\""+NEWLINE+"Content-Type:text/plain"+NEWLINE;    }    public String getDisposition(String name,String filePath) {        String type = HttpHelper.getContentType(filePath);        return getBoundary()+"Content-Disposition: form-data; name=\"" + name + "; filename=\"" + filePath + "\""+NEWLINE+"Content-Type:" + type + ""+NEWLINE;    }}