package com.song.net.http;import com.song.io.FileHelper;import com.song.io.StreamHelper;import com.song.lang.StringHelper;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.io.OutputStream;/** * description:     HttpResponse * author:          song * createDate:      2017/9/27 */public class HttpResponse {    public HttpServletResponse getResponse() {        return response;    }    protected HttpServletResponse response;    public HttpResponse(HttpServletResponse response) {        this.response = response;    }    public void reset() {        this.response.reset();    }    public void write(String s) throws IOException {        write(StringHelper.getBytes(s));    }    /**     * 输入流中写入文件     * @param filePath     * @throws IOException     */    public void writeFile(String filePath) throws IOException {        write(FileHelper.getBytes(filePath));    }    /**     * 输入流中写入内容     * @param bytes     * @throws IOException     */    public void write(byte[] bytes) throws IOException {        OutputStream stream = null;        try {            stream = response.getOutputStream();            stream.write(bytes);        } finally {            StreamHelper.close(stream);        }    }}