package com.song.net.http;import com.song.common.Result;import com.song.util.MapHelper;/** * description:     HttpResult * author:          song * createDate:      2017/9/27 */public class HttpResult extends Result {    private String code;    public HttpResult(boolean isSuccess, String msg) {        super(isSuccess, msg);    }    public HttpResult(boolean isSuccess) {        super(isSuccess);    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;        this.add("code", code);    }    public String toString() {        return "{" + MapHelper.join(this.getAttrs(), ":", ",", "\"") + "}";    }}