package com.song.toolkit.crawler.lianjia;import com.song.lang.StringHelper;/** * description: * author:          song * createDate:      2017/12/12 */public class LianJiaHelper {    /**     * 解析单价     * @param content     * @return     */    public static String parseUnitPrice(String content) {        if (!StringHelper.isEmpty(content)) {            return StringHelper.replaceAll(content, "", "元/平米", "单价");        }        return content;    }    public static String[] parseHouseInfo(String content) {        if(!StringHelper.isEmpty(content)){            return content.split("\\|");        }        return null;    }}