package com.song.toolkit.serializer;import com.song.lang.StringHelper;/** * description: * author:          song * createDate:      2017/10/19 */public class SerializerFactory {    private static JSONSerializer jsonInstance;    private static XMLSerializer xmlInstance;    private static TXTSerializer txtSerializer;    public static JSONSerializer getJSONInstance() {        if (jsonInstance == null) {            jsonInstance = new JSONSerializer();        }        return jsonInstance;    }    public static XMLSerializer getXMLInstance() {        if (xmlInstance == null) {            xmlInstance = new XMLSerializer();        }        return xmlInstance;    }    public static TXTSerializer getTXTInstance() {        if (txtSerializer == null) {            txtSerializer = new TXTSerializer();        }        return txtSerializer;    }    /**     * 根据类型动态获取数据序列化对象，默认json序列化     *     * @param type xml,json     * @return     */    public static IDataSerializer getInstance(String type) {        if (StringHelper.isEmpty(type)) {            return null;        }        type = type.toLowerCase();        if (type.equals("json")) {            return getJSONInstance();        } else if (type.equals("xml")) {            return getXMLInstance();        } else if (type.equals("txt")) {            return getTXTInstance();        }        return null;    }}