package com.studio.api.base;import com.studio.model.BaseModel;import org.springframework.beans.factory.annotation.Autowired;import tk.mybatis.mapper.common.Mapper;import java.util.List;/** * description: * author:          song * createDate:      2017/12/28 */public abstract class BaseService<T extends BaseModel> implements IBaseService<T> {    @Autowired    protected Mapper<T> baseDao;    public T findById(Object id) {        return baseDao.selectByPrimaryKey(id);    }    public T findOne(T t) {        return baseDao.selectOne(t);    }    public List<T> find(T t) {        return baseDao.select(t);    }    public int count(T t) {        return baseDao.selectCount(t);    }    public List<T> findAll() {        return baseDao.selectAll();    }}