package com.studio.api.service.impl;import com.studio.api.base.BaseService;import com.studio.api.dao.IColumnDao;import com.studio.api.service.IColumnService;import com.studio.model.Column;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;/** * description: * author:          song * createDate:      2018/1/4 */@Servicepublic class ColumnService extends BaseService<Column> implements IColumnService {    @Resource    private IColumnDao columnDao;    public List<Column> getList(String tableId) {        return columnDao.getByTableId(tableId);    }}