package com.studio.builder.metadata;import com.song.db.DBConnectionConfig;import com.song.db.DBType;/** * description: * author:          song * createDate:      2017/10/25 */public class DbStructFactory {    /**     * 根据数据库类型和连接字符串获取数据库结构读取器     *     * @param config     * @return     * @throws Exception     */    public static DbStructReader create(DBConnectionConfig config) throws Exception {        DBType dbType = config.getDbType();        switch (config.getDbType()) {            case Oracle:                return new OracleStructReader(config);            case MySQL:                return new MySQLStructReader(config);            case SQLServer:                return new SQLServerStructReader(config);        }        throw new Exception(dbType + " database is not supported");    }}