package com.studio.common.result;import com.song.common.Result;import java.util.ArrayList;import java.util.List;/** * description: * author:          song * createDate:      2018/1/24 */public class UploaderResult extends Result {    public UploaderResult() {        this.files = new ArrayList<UploaderFile>();    }    private List<UploaderFile> files;    public List<UploaderFile> getFiles() {        return files;    }    public void setFiles(List<UploaderFile> files) {        this.files = files;    }    public void addFile(UploaderFile file) {        this.files.add(file);    }}