package com.studio.common.result;import com.song.net.http.HttpResult;/** * description:     Ajax通用返回类 * author:          song * createDate:      2018/1/8 */public class ViewResult extends HttpResult {    public ViewResult(boolean isSuccess, String msg) {        super(isSuccess, msg);    }    public ViewResult(boolean isSuccess) {        super(isSuccess);    }    public ViewResult() {    }    public ViewResult(boolean isSuccess, String msg, String code) {        super(isSuccess, msg, code);    }    public ViewResult(boolean isSuccess, String msg, String code, Object data) {        super(isSuccess, msg, code);        this.data = data;    }    private Object data;    public Object getData() {        return data;    }    public void setData(Object data) {        this.data = data;    }}