package com.studio.model;import com.song.db.DBType;import javax.persistence.Table;/** * description: * author:          song * createDate:      2017/10/25 */@Table(name = "studio_Connection")public class Connection extends BaseModel {    private String connectionName;    private DBType dbType;    private String connectionString;    public String getConnectionName() {        return connectionName;    }    public void setConnectionName(String connectionName) {        this.connectionName = connectionName;    }    public DBType getDbType() {        return dbType;    }    public void setDbType(DBType dbType) {        this.dbType = dbType;    }    public String getConnectionString() {        return connectionString;    }    public void setConnectionString(String connectionString) {        this.connectionString = connectionString;    }}