package com.song;import com.song.ui.easyui.EasyuiBuilder;/** * Created by song on 2017/9/23. */public class TestBuilder {    public static void main(String[] args) {        runHtmlBuilder();    }    private static void runHtmlBuilder() {        EasyuiBuilder builder = new EasyuiBuilder("input");        builder.attr("href", "#").attr("id", "1");        builder.addClass("cls1");        builder.setContent("content");        builder.css("border", "1px solid red");        builder.option("region", "center").option("height", 100);        String html = builder.toString();        System.out.println(html);    }}