package song.service.user.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import song.service.user.base.BaseController;import song.service.user.model.Demo;import song.service.user.service.impl.DemoServiceImpl;import java.util.List;/** * description: * author:          song * createDate:      2017/12/28 */@RequestMapping("/demo")@RestControllerpublic class DemoController extends BaseController {    @Autowired    private DemoServiceImpl demoService;    @RequestMapping(value = "/get", method = RequestMethod.GET)    public List<Demo> getList(String name,                              @RequestParam(defaultValue = "1") int pageIndex,                              @RequestParam(defaultValue = "10") int pageSize) {        return demoService.getByName(name);    }}